import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {CinemaService} from "../services/cinema.service";

@Component({
  selector: 'app-cinema',
  templateUrl: './cinema.component.html',
  styleUrls: ['./cinema.component.css']
})
export class CinemaComponent implements OnInit{
  // @ts-ignore
  public villes;

  // @ts-ignore
  public cinemas;
  // @ts-ignore
  public salles;

  // @ts-ignore
  public currentVille;

  // @ts-ignore
  public currentCinema;
  constructor(private cinemaservice:CinemaService) {
  }
  ngOnInit(): void {
    this.cinemaservice.getVilles()
      .subscribe(data=>{
        this.villes=data;
        console.log(this.villes);
      },error => {
        console.log(error);
      })
  }

  // @ts-ignore
  OnGetCinema(v): void {
    this.currentVille=v;
    this.cinemaservice.getCinemas(v)
      .subscribe(data=>{
        this.cinemas=data;
      },err => {
        console.log(err);
      })

  }

  // @ts-ignore
  OnGetSalle(c): void {
    this.cinemaservice.getSalles(c)
      .subscribe(data=>{
      this.salles=data;
      },err=>{
    console.log(err);
})
  }
}
<div class="container">
  <div class = "row">
    <div class ="col-md-4">
      <ul *ngIf="villes" class="list-group">
        <li [ngClass]= "v==currentVille?'active':''" *ngFor="let v of villes._embedded.villes" class="list-group-item clickable"
        (click)="OnGetCinema(v)">
          {{v.nom}}

        </li>
      </ul>
    </div>
    <div class ="col-md-8">
      <div class = "panel panel-default">
        <div class="panel-heading">Liste des Cinémas</div>
        <div class = "panel-body">
          <ul *ngIf="cinemas" class = "nav nav-pills clickable">
            <li *ngFor="let c of cinemas._embedded.cinemas">
              <a (click)="OnGetSalle(c)">{{c.nom}}</a>
            </li>
          </ul>
          <div class ="row">
            <div *ngFor="let s of salles._embedded.cinemas"></div>
            <div class="panel"></div>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>
