import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {CinemaService} from "../services/cinema.service";

@Component({
  selector: 'app-cinema',
  templateUrl: './cinema.component.html',
  styleUrls: ['./cinema.component.css']
})
export class CinemaComponent implements OnInit{
  // @ts-ignore
  public villes;

  // @ts-ignore
  public cinemas;
  // @ts-ignore
  public salles;

  // @ts-ignore
  public currentVille;

  // @ts-ignore
  public currentCinema;
  // @ts-ignore
  public currentProjection;
  // @ts-ignore
  private ticket;

  // @ts-ignore
  private selectedTickets;
  constructor(public cinemaservice:CinemaService) {
  }
  ngOnInit(): void {
    this.cinemaservice.getVilles()
      .subscribe(data=>{
        this.villes=data;
      },error => {
        console.log(error);
      })
  }

  // @ts-ignore
  OnGetCinema(v): void {
    this.currentVille=v;
    this.salles=undefined;
    this.cinemaservice.getCinemas(v)
      .subscribe(data=>{
        this.cinemas=data;
      },err => {
        console.log(err);
      })

  }
  // @ts-ignore
  OnGetSalles(c): void {
    this.currentCinema=c;
    this.cinemaservice.getSalles(c)
      .subscribe(data=>{
        this.salles=data;
        // @ts-ignore
        this.salles._embedded.salles.forEach(salle=>{
          this.cinemaservice.getProjections(salle)
            .subscribe(data=>{
              salle.projections=data;
            },err => {
              console.log(err);
            })
        })
      },err => {
        console.log(err);
      })

  }

  // @ts-ignore
  OnGetTicketPlace(p) {
    this.currentProjection=p;
    this.cinemaservice.getTicketsPlaces(p)
      .subscribe(data=>{
        this.currentProjection.tickets=data;
        this.selectedTickets=[];
      },err => {
        console.log(err);
      })

  }
  // @ts-ignore

  OnSelectTicket(t){
    if(!t.selected){
      t.selected=true;
      this.selectedTickets.push(t);
    }
    t.selected=false;
    this.selectedTickets.splice(this.selectedTickets.indexOf(t),1);
  }

  // @ts-ignore

  getTicketClass(t) {
    let str="btn ticket ";
    if(t.reserve==true){
      str+="btn-danger";
    }
    else if(t.seleted){
      str+="btn-warning"
    }
    else{
      str+="btn-success"
    }
    return str;
  }
}
<div class="container">
  <div class = "row">
    <div class ="col-md-3">
      <ul *ngIf="villes" class="list-group">
        <li [ngClass]= "v==currentVille?'active':''" *ngFor="let v of villes._embedded.villes" class="list-group-item clickable"
        (click)="OnGetCinema(v)">
          {{v.nom}}
        </li>
      </ul>
    </div>
    <div class ="col-md-9">
      <div class = "panel panel-default">
        <div class="panel-heading">Liste des Cinémas</div>
        <div class = "panel-body">
          <ul *ngIf="cinemas" class = "nav nav-pills ">
            <li *ngFor="let c of cinemas._embedded.cinemas" [ngClass]= "c==currentCinema?'active':''" class="clickable">
              <a (click)="OnGetSalles(c)">{{c.nom}}</a>
            </li>
          </ul>
          <div class ="row" *ngIf="salles">
            <div *ngFor="let s of salles._embedded.salles">
              <div class = "col-md-6">
                <div class="panel panel-default">
                  <div class = "panel panel-heading">{{s.name}}</div>
                  <div class = "panel-body" *ngIf="s.projections">
                    <div class="row">
                      <div class="col-md-5">
                        <img width="130" src="{{cinemaservice.host+'/imageFilm/'+s.projections._embedded.projections[0].film.id}}">
                      </div>
                      <div class="col-md-7">
                        <ul class="list-group">
                          <li class="list-group-item">Séances:</li>
                          <li *ngFor="let p of s.projections._embedded.projections" (click)="OnGetTicketPlace(p)" [ngClass]= "p==currentProjection?'active':''" class = "list-group-item clickable">
                            {{p.seance.heureDebut}} => Prix : {{p.prix|number:'0.00'}} DH
                          </li>
                        </ul>
                      </div>
                    </div>
                    <div *ngIf="currentProjection ">
                        <div *ngIf="currentProjection.salle.id==s.id ">
                          <div class="row" *ngIf="currentProjection.tickets">
                            <button [disabled]="t.reserve" class="{{getTicketClass(t)}}" *ngFor="let t of currentProjection.tickets._embedded.tickets" (click)="OnSelectTicket(t)">
                              {{t.place.numero}}
                            </button>
                          </div>
                        </div>
                      <div>
                        <form #f="ngForm" (ngSubmit)="OnPayTickets()" >
                          <div class="form-group">
                            <label></label>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>
